import { Component } from '@angular/core';

@Component({
  selector: 'app-lugares',
  templateUrl: './lugares.component.html',
  styleUrls: ['./lugares.component.css']
})
export class LugaresComponent {

}
<!DOCTYPE html>
<html lang="en">
    <head>
        <style>
            .slide {
                position: relative;
                box-shadow: 0px 1px 6px rgba(0, 0, 0, 0.64);
                margin-top: 26px;
            }
            .slide-inner {
                position: relative;
                overflow: hidden;
                width: 100%;
                height: calc( 300px + 3em);
            }
            .slide-open:checked + .slide-item {
                position: static;
                opacity: 100;
            }
            .slide-item {
                position: absolute;
                opacity: 0;
                -webkit-transition: opacity 0.6s ease-out;
                transition: opacity 0.6s ease-out;
            }
            .slide-item img {
                display: block;
                height: auto;
                max-width: 100%;
            }
            .slide-control {
                background: rgba(0, 0, 0, 0.28);
                border-radius: 50%;
                color: #fff;
                cursor: pointer;
                display: none;
                font-size: 40px;
                height: 40px;
                line-height: 35px;
                position: absolute;
                top: 50%;
                -webkit-transform: translate(0, -50%);
                cursor: pointer;
                -ms-transform: translate(0, -50%);
                transform: translate(0, -50%);
                text-align: center;
                width: 40px;
                z-index: 10;
            }
            .slide-control.prev {
                left: 2%;
            }
            .slide-control.next {
                right: 2%;
            }
            .slide-control:hover {
                background: rgba(0, 0, 0, 0.8);
                color: #aaaaaa;
            }
            #slide-1:checked ~ .control-1,
            #slide-2:checked ~ .control-2,
            #slide-3:checked ~ .control-3 {
                display: block;
            }
            .slide-indicador {
                list-style: none;
                margin: 0;
                padding: 0;
                position: absolute;
                bottom: 2%;
                left: 0;
                right: 0;
                text-align: center;
                z-index: 10;
            }
            .slide-indicador li {
                display: inline-block;
                margin: 0 5px;
            }
            .slide-circulo {
                color: #828282;
                cursor: pointer;
                display: block;
                font-size: 35px;
            }
            .slide-circulo:hover {
                color: #aaaaaa;
            }
            #slide-1:checked ~ .control-1 ~ .slide-indicador 
                 li:nth-child(1) .slide-circulo,
            #slide-2:checked ~ .control-2 ~ .slide-indicador 
                  li:nth-child(2) .slide-circulo,
            #slide-3:checked ~ .control-3 ~ .slide-indicador 
                  li:nth-child(3) .slide-circulo {
                color: #428bca;
            }
            #titulo {
                width: 100%;
                position: absolute;
                padding: 0px;
                margin: 0px auto;
                text-align: center;
                font-size: 27px;
                color: rgba(255, 255, 255, 1);
                font-family: 'Open Sans', sans-serif;
                z-index: 9999;
                text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.33), 
                     -1px 0px 2px rgba(255, 255, 255, 0);
            }
        </style>
    </head>

<body>
    <div style="width: 100%; height: 40vh; background-color: #ff8c28; margin: 0 auto 0 0; display: flex; justify-content: center; align-items: center; flex-direction: column;">
        <div style="position: relative ;bottom: 10px;justify-content: center; align-items: center; flex-direction: column;"> 
            <div style="width: 100%; display: flex; justify-content: center;">
                <h1 style="color: white; text-shadow: 2px 2px 4px #000000, 2px 2px 4px #FF0000, 2px 2px 4px #00FF00, 2px 2px 4px #6cb7e3; font-size: 3em; font-family: Arial, sans-serif; width: 50%; text-align: center;">
                    Lugares Imperdibles de Silvia
                </h1>
            </div>

            <div id="barra" style="width:100%; height: 10px; background-color: #ffffff; border-radius: 5px; margin-top: 6px;"> 
            </div>
        </div>
    </div>
    <div class="lugares-1">
        <div class="lugares-2">
            <div class="lugares-3" style= " background-image: url('assets/image/iglesiabelen.jpg');"
            onmouseover="this.style.transform='scale(1.1)';" onmouseout="this.style.transform='scale(1)';">

                <!-- <img src="assets/image/iglesiabelen.jpg" alt="Imagen 1" style="max-width: 100%; max-height: 100%; border-radius: 10px;"> -->
            </div>
            <div class="lugares-4" style="width: 50vh; height: 50vh; margin: 10px; display: flex; justify-content: center; align-items: center; ">
                <!-- <img src="assets/image/iglesiabelen.jpg" alt="Imagen 1" style="max-width: 100%; max-height: 100%; border-radius: 10px;"> -->
                <div style="display: grid; grid-template-columns: repeat(2, 1fr); grid-template-rows: repeat(2, 1fr); gap: 10px; width: 100%; height: 100%;">
                    <div style="width: 100%; height: 100%; display: flex; justify-content: center; align-items: center; background-image: url('assets/image/lagochimansilviacauca.webp'); background-size: cover; background-position: center; border-radius: 5px ;transition: transform 0.3s ease-in-out; "
                    onmouseover="this.style.transform='scale(1.1)';" onmouseout="this.style.transform='scale(1)';">
                        <!-- <img src="assets/image/iglesiabelen.jpg" alt="Imagen 1" style="max-width: 100%; max-height: 100%; border-radius: 10px;"> -->
                    </div>
                    <div style="width: 100%; height: 100%; display: flex; justify-content: center; align-items: center; background-image: url('assets/image/birimbi.jpg'); background-size: cover; background-position: center; border-radius: 5px ;transition: transform 0.3s ease-in-out;"
                    onmouseover="this.style.transform='scale(1.1)';" onmouseout="this.style.transform='scale(1)';">
                        <!-- <img src="assets/image/iglesiabelen.jpg" alt="Imagen 1" style="max-width: 100%; max-height: 100%; border-radius: 10px;"> -->
                    </div>
                    <div style="width: 100%; height: 100%; display: flex; justify-content: center; align-items: center; background-image: url('assets/image/mirador de la aurora.jpg'); background-size: cover; background-position: center; border-radius: 5px ;transition: transform 0.3s ease-in-out;"
                    onmouseover="this.style.transform='scale(1.1)';" onmouseout="this.style.transform='scale(1)';">
                        <!-- <img src="assets/image/iglesiabelen.jpg" alt="Imagen 1" style="max-width: 100%; max-height: 100%; border-radius: 10px;"> -->
                    </div>
                    <div style="width: 100%; height: 100%; display: flex; justify-content: center; align-items: center; background-image: url('assets/image/laplaya.jpg'); background-size: cover; background-position: center; border-radius: 5px ;transition: transform 0.3s ease-in-out;"
                    onmouseover="this.style.transform='scale(1.1)';" onmouseout="this.style.transform='scale(1)';">
                        <!-- <img src="assets/image/iglesiabelen.jpg" alt="Imagen 1" style="max-width: 100%; max-height: 100%; border-radius: 10px;"> -->
                    </div>
                </div>

            </div>
        </div>

    </div>
    <div style="width: 100%; height: 60vh; background-color: #ffffff; margin: 0px; display: flex; justify-content: center; align-items: center; flex-direction: column;">
        <h3 style="text-align: center; font-size: 300%;">Descubre Silvia</h3>
        <p style="text-align: center; font-size: 150%; width: 80%; margin: 0 auto;">
            Silvia, en el corazón del Cauca, ofrece paisajes únicos y una rica cultura indígena. Al subir a la Colina de Belén, descubrirás una pequeña iglesia con una vista panorámica del valle y las montañas, ideal para fotos. Muy cerca, el Lago Chimán te invita a disfrutar de la tranquilidad de sus aguas rodeadas de naturaleza.

            Para probar los sabores de la región, visita el Restaurante Birimbi, donde los platos locales destacan. Luego, el Mirador de la Aurora te regalará un atardecer inolvidable con sus vistas panorámicas.

            El Tejar es un rincón rural perfecto para disfrutar de una caminata o un picnic, mientras que La Playa te sorprenderá con su famosa trucha fresca, en un entorno ideal para una comida al aire libre.

            Por último, en el centro de Silvia, la Iglesia Nuestra Señora de Chiquinquirá destaca como símbolo de la devoción local.

            Visitar Silvia es una experiencia auténtica que mezcla naturaleza, cultura y tradición en cada rincón.
        </p>


    </div>

    <div style="width: 100%; height: 60vh; background-color: #28a5ff; margin: 0px; display: flex; justify-content: center; align-items: center; flex-direction: column;">
        <div class="slide">
            <div class="slide-inner">
                <input class="slide-open" type="radio" id="slide-1" 
                      name="slide" aria-hidden="true" hidden="" checked="checked">
                <div class="slide-item">
                    <img src="assets/image/eltejarsilviacauca.jpg"style="width:60vh">
                </div> 
                <input class="slide-open" type="radio" id="slide-2" 
                      name="slide" aria-hidden="true" hidden="">
                <div class="slide-item">
                    <img src="assets/image/iglesia9.jpg"style="width:60vh">
                </div>
