import { Component } from '@angular/core';

@Component({
  selector: 'app-silvia',
  templateUrl: './silvia.component.html',
  styleUrls: ['./silvia.component.css']
})
export class SilviaComponent {

}

<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Silvia Page</title>
</head>
<body style="background-color: green;  background-image: url('assets/image/conocesilvia.webp');
background-size: cover;
background-position: center;
background-repeat: no-repeat;
height: 100vh;
margin: 0;">
<div style="height: 50vh; display: flex; justify-content: center; align-items: center;">
  <h1 style="text-align: center; color: white; text-shadow: 2px 2px 4px #000000, 2px 2px 4px #FF0000, 2px 2px 4px #00FF00, 2px 2px 4px #0000FF; font-size: 3em;">
    Un Encuentro con la Cultura y Naturaleza del Cauca,
  </h1>
</div>

  <div style="background-image: url('path/to/your/image.jpg'); background-size: cover; background-position: center; width: 100%; height: 100vh;">
  </div>
</body>
</html>
